mapper.select:
SELECT pb.id, pb.create_at, pb.`status` AS status_book, p.name AS name_pitch, p.address, u.email, u.fullname, u.phone_number, ts.start_time, ts.end_time, pt.price FROM pitch_booking pb INNER JOIN pitch p ON pb.pitch_time_pitch_id = p.id INNER JOIN time_slot ts ON pb.pitch_time_time_slot_id = ts.id INNER JOIN user u ON pb.user_id = u.id INNER JOIN pitch_time pt ON p.id = pt.pitch_id AND ts.id = pt.time_slot_id WHERE pb.status IN
#{status}
ORDER BY pb.status ASC
