mapper.update:
UPDATE pitch_time
 SET
 status = #{status}
 WHERE pitch_id = #{pitch_id} AND time_slot_id = #{time_slot_id}

UPDATE pitch_time
SET `status` = #{status}
WHERE pitch_id = #{pitchId} AND time_slot_id = #{timeSlotId}

mapper.select:
SELECT *
FROM pitch_time
WHERE pitch_id = #{pitchId} AND time_slot_id = #{timeSlotId}
